Condition.ANIMATION_OFFSET_CODE:
(A:GENERAL ENG PCT MAX RPM:#ID#, Percent over 100) sp0
l0 0 > if{
(E:SIMULATION TIME, seconds) #INITIAL_VARIATION# + s1 1.75 * cos
l1 #INITIAL_VARIATION# + 3 * cos 2.2 * cos *
#VARIATION_RANGE# *
1 l0 0.3 - 0 max 0.7 / - *
sp0
} els{
0 sp0
}
l0 +

UseTemplate.SET_STATE_EXTERNAL:
(O:StopwatchState) ++ s0 0 0 2 l0 rng ? (>O:StopwatchState)
(O:StopwatchState) 2 != if{
(E:SIMULATION TIME, seconds) (>I:StopWatchTimeCurSeconds)
(E:SIMULATION TIME, seconds) (>I:StopWatchTimeStartSeconds)
}

UseTemplate.ANIM_CODE:
(A:NAV RADIAL ERROR:#ID#, degrees) sp0
l0 90 > if{
180 l0 - sp0
} els{
l0 -90 < if{
-180 l0 - sp0
}
}
(A:AUTOPILOT BACKCOURSE HOLD, Bool) if{
l0 -1 * 0.9 / 50 +
} els{
l0 0.9 / 50 +
}

UseTemplate.OVERRIDE_ANIM_CODE:
(I:XMLVAR_GFORCE_Sync_MinMax) if{ (O:_GFORCE_Max_Value) #SYNC_DELTA_VALUE# - (>O:_GFORCE_Max_Value) }
(A:G FORCE, GForce) (O:_GFORCE_Max_Value) > if{ (A:G FORCE, GForce) #MAX_VALUE# min (>O:_GFORCE_Max_Value) }
(O:_GFORCE_Max_Value) #CLAMP_CODE# (>O:_GFORCE_Max_Value)
(O:_GFORCE_Max_Value) #ANIM_CODE_ADJUSTMENT#

(I:XMLVAR_GFORCE_Sync_MinMax) if{ (O:_GFORCE_Min_Value) #SYNC_DELTA_VALUE# + (>O:_GFORCE_Min_Value) }
(A:G FORCE, GForce) (O:_GFORCE_Min_Value) < if{ (A:G FORCE, GForce) #MIN_VALUE# max (>O:_GFORCE_Min_Value) }
(O:_GFORCE_Min_Value) #CLAMP_CODE# (>O:_GFORCE_Min_Value)
(O:_GFORCE_Min_Value) #ANIM_CODE_ADJUSTMENT#
